.footer {
  padding: 6rem 11.8rem 6rem 11.8rem;

  @include media("<=tablet") {
    padding: 8.6rem 4rem 3rem;
  }

  @include media("<phone") {
    padding: 8.6rem 2rem 3rem;
  }
}

.footer__information {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  row-gap: 4rem;

  grid-template-areas:
    "location media email"
    "logos sponsors sponsors";

  @include media("<=tablet") {
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: 1fr;
    grid-template-areas:
      "location media email"
      "logos logos sponsors";
  }

  @include media("<phone") {
    grid-template-areas:
      "location media"
      "email email"
      "logos logos"
      "sponsors sponsors";
    grid-template-columns: repeat(2, 1fr);
    row-gap: 3rem;
  }

  & .footer__location {
    grid-area: location;
  }

  & .footer__socials {
    grid-area: media;

    @include media("<=tablet") {
      justify-self: start;
    }

    @include media("<phone") {
      justify-self: end;
    }
  }

  & .footer__email {
    grid-area: email;
  }

  & .footer__partners {
    grid-area: logos;
  }

  & .footer__sponsors {
    justify-self: start;
    grid-area: sponsors;
  }

  
}

.footer__location {
}

.footer__title {
  margin-bottom: 1.2rem;
  white-space: nowrap;

  .a {
    color: rgba($color: $color-black, $alpha: 0.6);
  }
}

.footer__label {
}

.footer__socials {
  @include media("<=tablet") {
    justify-self: center;
  }

  @include media("<phone") {
    justify-self: end;
  }
}

.footer__partners {
  @include media("<=tablet") {
    grid-area: 2 / 1 / 3 / 3;
  }

  & .footer__images__grid__image {
    &:last-child {
      @include media("<=tablet") {
        height: 100%;
        width: 6rem;
      }

      @include media("<phone") {
      }
    }
  }
}

.footer__sponsors {
  justify-self: end;

  & .footer__images__grid__image {
    &:last-child {
      @include media("<=tablet") {
        height: 100%;
        width: 8.5rem;
      }

      @include media("<phone") {
        height: 100%;
        width: 7rem;
      }
    }
  }
}

.footer__images__grid {
  align-items: flex-start;
  display: flex;
  flex-wrap: wrap;

  @include media("<phone") {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
  }
}

.footer__images__grid__image {
  height: 3rem;
  margin-right: 3.5rem;
  width: auto;
  display: block;

  &:first-child {
    @include media("<=tablet") {
      width: 5.6rem;
    }
  }

  &:nth-child(2) {
    @include media("<=tablet") {
      width: 11.8rem;
    }

    @include media("<phone") {
      height: 2.5rem;
      width: 10rem;
    }
  }

  &:last-child {
    margin-right: 0;

    @include media("<=tablet") {
      width: 8.5rem;
    }

    @include media("<phone") {
      height: 2.5rem;
    }
  }
}

.footer__marquee {
  @extend %title-60;

  align-items: center;
  display: flex;
  margin-bottom: 4rem;
  margin-top: 8rem;
  position: relative;
  overflow: hidden;
  white-space: nowrap;

  a {
    display: inline-block;
    line-height: 0.8;
    padding: 6rem 6rem 6rem 0;
    vertical-align: middle;

    @include media("<=tablet") {
      padding: 4rem 2rem 4rem 0;
    }

    @include media("<phone") {
      padding: 3rem 2rem 3rem 0;
    }
  }

  &::before,
  &::after {
    background-color: $color-black;
    content: "";
    height: 0.2rem;
    left: 0;
    position: absolute;
    width: 100%;

    @include media("<phone") {
      height: 0.1rem;
    }
  }

  &::before {
    top: 0;
  }

  &::after {
    bottom: 0;
  }

  @include media("<=tablet") {
    margin-top: 6rem;
  }
}

