  margin: 8.6rem 0;

  span {
    @extend %title-40;

    margin-right: 2rem;
    opacity: 0.3;

    @include media("<phone") {
      font-size: 2rem;
      font-weight: 500;
      margin-right: 1rem;
    }
  }

  @include media("<phone") {
    margin: 6rem 0;
    font-weight: 500;
  }
}

.background__label {
  @extend %title-40;

  font-family: $font-poppins;

  span {
    font-weight: 800;
  }

  @include media("<phone") {
    font-size: 1.8rem;
  }
}

/* SERVICES & REASONS */

.reasons {
  background-color: $color-black;
  padding: 15rem 11.8rem;

  @include media("<=tablet") {
    padding: 8.6rem 4rem;
  }

  @include media("<phone") {
    padding: 8.6rem 2rem;
  }
