  @include media("<phone") {
    font-size: 1.8rem;
  }
}

.reasons__grid {
  align-content: center;
  column-gap: 4rem;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  row-gap: 4rem;
  margin-top: 12rem;

  @include media("<=tablet") {
    column-gap: 3rem;
    grid-template-columns: repeat(2, 1fr);
    row-gap: 3rem;
  }

  @include media("<phone") {
    grid-template-columns: 1fr;
    margin-top: 6rem;
  }
}

.reasons__grid__item {
  border: 0.2rem solid $color-white;
  padding: 7.4rem 8.7rem;

  &:last-child {
    background-color: $color-white;

    & .reasons__grid__item__label {
      color: $color-black;
      font-weight: 500;
